mapper.select:


    sc.is_deleted = 0

        and sc.section_id = #{sectionId}


        and sc.user_id = #{userId}


        and sc.parent_id = #{parentId}


        and s.course_id = #{params.courseId}


        and sc.content like concat('%', #{params.content}, '%')


        and u.real_name like concat('%', #{params.userName}, '%')


order by sc.create_time desc


where sc.parent_id = #{parentId}
  and sc.is_deleted = 0
order by sc.create_time asc

select count(*) from section_comment
where section_id = #{sectionId}
  and is_deleted = 0

select count(*) from section_comment
where parent_id = #{parentId}
  and is_deleted = 0

mapper.update:
update section_comment
content = #{content}, update_time = #{updateTime},
where id = #{id}

update section_comment
set is_deleted = 1, delete_time = now()
where id = #{id}

update section_comment
set is_deleted = 1, delete_time = now()
where parent_id = #{parentId}
